package ControlPanel.GUI.BillboardsPane;

import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.awt.*;
import java.io.File;

import static org.junit.jupiter.api.Assertions.*;

public class CreateBillboardsTest {

    @BeforeEach
    void setUp() {
    }

    @AfterEach
    void tearDown() {
    }
    @Test
    public void getFileExtension(){
        String fullname="userInformation.xml";
        assertEquals("xml",CreateBillboards.getFileExtension(fullname));
    }
    @Test
    public void encodeFileToBase64Binary() throws Exception {
        String imge_1png_base64="";

        File file;
        file =new File("1.png");
        assertEquals(imge_1png_base64,CreateBillboards.encodeFileToBase64Binary(file));
    }
    @Test
    public void convertColorToHexadecimal(){
        Color color;
        color=new Color(255,127,80);
        assertEquals("#FF7F50",CreateBillboards.convertColorToHexadecimal(color));
    }
    @Test
    public void createXMLString(){
        String billboardColour="white";
        String message="nice";
        String messageColour="blue";
        String info="cool cool cool";
        String infoColour="pink";
        String imgData="";
        String XML="<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" +
                "<billboard background=\"white\">\n" +
                "<message colour=\"blue\">nice</message>\n" +
                "<picture data=\"\" />\n" +
                "<information colour=\"pink\">cool cool cool</information>\n" +
                "</billboard>";
        assertEquals(XML,CreateBillboards.createXMLString(billboardColour,message,messageColour,info,infoColour,imgData));
    }
}